package Java;

public class SumOfArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
			int[] sumArr = new int[] {1,1,1,1,1,1};
			int sum = 0;
			for (int i = 0; i < sumArr.length; i++) {
				sum = sum + sumArr[i];
			}
		System.out.println("sum of array= " +sum);
			}
		}

